DO $$
DECLARE data integer;
    DECLARE id integer;
        DECLARE area integer;

BEGIN

SELECT dataTypeId
               from dataType
               where dataTypeName = 'decimal' into data;

SELECT areaTypeId
from areaType
where areaTypeName = 'localauthority' into area;

values ('totalcuts', area, data);

SELECT statTypeId
               from statType
               where statTypeName = 'totalcuts' and areaTypeId = area into id;



END $$;